local Lg=locale=="zh" or locale=="zhr" name=Lg and "精炼宝箱" or "Refine Chest" author="豌豆射手" version="1.0.7" description=Lg and "版本：" .. version .. "\n\n你是否感到反复制作木板、绳子非常浪费时间？\n制作一个精炼宝箱，将材料放入其中，就可以完成这一切。不再烦恼！\n使用3个木板+6个花瓣在󰀔制作，精炼栏(󰀧)\n\n在精炼开始前请确认放入箱子的材料！\n\n版本说明：\n　　在精炼宝箱中放入画好的小木牌作为精炼模板，这适用于mod物品的制作.\n\n在创意工坊获取更多细节.\n\n允许配置选项。" or "Version : " .. version .. "\n\nDo you find it a waste of time to make boards and ropes repeatedly?\nMake a refining treasure chest and put the materials into it to complete all this. No more trouble!\nUse 3 boards + 6 petals to make it in 󰀔,Refine column(󰀧).\nPlease confirm the materials put into the chest before refining!\n\nVersion Description:\n　Put painted \"minisign\" into \"Refine Chest\" as template,which is suitable for the production of mod items.\n\nGet more details at steam workshop.\n\nAllow configuration options." dst_compatible=true dont_starve_compatible=false reign_of_giants_compatible=false shipwrecked_compatible=false all_clients_require_mod=true api_version=6 api_version_dst=10 forumthread="" icon_atlas="modicon.xml" icon="modicon.tex" server_filter_tags={"auto","自动","精炼",} local base_options={{description=Lg and "允许" or "Enable",data=true},{description=Lg and "不允许" or "Disable",data=false},} configuration_options={{name="language",label=Lg and "语言" or "Language",options={{description="简体中文",data=2},{description="English",hover="Part of the content is translated by machine",data=1},}, default=Lg and 2 or 1,},{name="refine_punishcost",label=Lg and "精炼消耗" or "Refine Cost",options={{description=Lg and "无" or "No",data=""},{description=Lg and "饥饿值" or "Hunger",hover=Lg and "每个1󰀎" or "1󰀎 each",data="hunger"},{description=Lg and "理智值" or "Sanity",hover=Lg and "每个1󰀓" or "1󰀓 each",data="sanity"},{description=Lg and "生命值" or "Health",hover=Lg and "每个1󰀍　→　󰀕" or "1󰀍 each　→　󰀕",data="health"},},default="hunger",},{name="refine_wait",label=Lg and "等待时间" or "Wait Time",hover=Lg and "允许反悔的时间(在精炼执行前)" or "Open the chest and try again(before 